import React from 'react';
import "./Card.css";
import star from "../../assets/svg/star.svg";
import { useQuery, gql } from "@apollo/client";

function Card(props) {
    const rating = props.cat.rating;
    const catId = props.cat.id;
    const REMOVE_CAT_MUTATION = gql`
    {
        mutation {
            removeCat(id: catId)
        }
    }`
    var stars = [];
    for (let index = 0; index < rating; index++) {
        stars.push(<img src={star}/>);
    }
    return (
        <div className="animalCard">
            <img className="main-img" src={props.cat.imageURL}/>
            <h2>
                {props.cat.catName}
            </h2>
            <div className="card-start" id="rating">
                {stars}
            </div>
            <div className="card-price" >
                <h4>{props.cat.description}</h4>
            </div>
            <div className="remove-cat">
                <button onClick={() => useQuery(REMOVE_CAT_MUTATION)}>Remover</button>
            </div>
        </div>
    )
}


function removeCat() {
    const handleClick = (e) => {
        e.preventDefault();
        console.log("clicou")
    }

    useQuery(REMOVE_CAT_MUTATION);
    return (
        <button onClick={handleClick}>Remover</button>
    )
}

export default Card
